import React from "react";
import Button from "elements/Button";

import "assets/scss/style.scss";

export default function Categories({ data }) {
  return data.map((categories, index1) => {
    return (
      <section className="container" key={`categories-${index1}`} ref={data.refProduct}>
        <h4 className="mb-3 title-category">{categories.name}</h4>
        <div className="category container-grid">
          {categories.itemId.length === 0 ? (
            <div className="row">
              <div className="col-auto align-items-center">There is no design at this category</div>
            </div>
          ) : (
            categories.itemId.map((item, index2) => {
              return (
                <div key={`categories-${index1}-item-${index2}`} className="item column-3 row-1">
                  <Button type="link" href={`/details/${item._id}`}>
                    <div className="card-featured">
                      <figure className="img-wrapper">
                        <img src={item.imageUrl} alt={item.name} className="img-contain" />
                      </figure>
                    </div>
                  </Button>
                  <div className="meta-wrapper text-center mt-3">
                    <Button type="link" className="d-block text-white" href={`/details/${item._id}`}>
                      <h5>{item.name}</h5>
                      <p>${item.price}</p>
                    </Button>
                  </div>
                </div>
              );
            })
          )}
        </div>
      </section>
    );
  });
}
