import React, { useState, useRef, useEffect } from "react";
import propTypes from "prop-types";

import { DatePicker } from "react-datepicker";

import "./index.scss";
import "react-datepicker/dist/react-datepicker.css";

import iconCalendar from "assets/images/icons/icon-calendar.svg";

export default function InputDate(props) {
  // const [selectDate, setSelectDate] = useState(new Date());
  // //   const [selectDate, setSelectDate] = useState(null);
  // const onChange = (selectDate) => {
  //   setSelectDate(selectDate);
  // };

  const { value, placeholder, name } = props;
  const [isShowed, setIsShowed] = useState(false);

  const datePickerChange = (value) => {
    const target = {
      target: {
        value: value.selection,
        name: name,
      },
    };
    props.onChange(target);
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  });

  const refDate = useRef(null);
  const handleClickOutside = (event) => {
    if (refDate && !refDate.current.contains(event.target)) {
      setIsShowed(false);
    }
  };

  const check = (focus) => {
    focus.indexOf(1) < 0 && setIsShowed(false);
  };

  const displayDate = `${value.selectDate}`;

  return (
    <div ref={refDate} className={["input-date mb-3", props.outerClassName].join(" ")}>
      <div className="input-group">
        <div className="input-group-prepend bg-gray-900">
          <span className="input-group-text">
            <img src={iconCalendar} alt="icon calendar" />
          </span>
        </div>
        <input readOnly type="text" className="form-control" value={displayDate} placeholder={placeholder} onClick={() => setIsShowed(!isShowed)} />

        {isShowed && (
          <div className="date-range-wrapper">
            <DatePicker editableDateInputs={true} onChange={datePickerChange} moveRangeOnFirstSelection={false} onRangeFocusChange={check} ranges={[value]} />
          </div>
        )}
      </div>
    </div>
  );
}

Date.propTypes = {
  value: propTypes.object,
  onChange: propTypes.func,
  placeholder: propTypes.string,
  outerClassName: propTypes.string,
};
