import React, { Component } from "react";
import { withRouter } from "react-router-dom";

import propTypes from "prop-types";

import Button from "elements/Button/Button";
import InputNumber from "elements/Form/InputNumber/InputNumber";

class BookingForm extends Component {
  state = {
    value: "1",
  };

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { data } = this.state;
    const { itemDetails } = this.props;

    return (
      <div className="card bordered" style={{ padding: "60px 80px" }}>
        <h4 className="mb-3">Start Booking</h4>
        <h5 className="h2 text-teal mb-4">
          ${itemDetails.price} <span className="text-gray-500 font-weight-light">per {itemDetails.unit}</span>
        </h5>

        <label htmlFor="duration">Berapa Baju yang Anda ingin beli?</label>
        <InputNumber max={1000000000000} onChange={this.handleChange} name="value" value={this.state.value} />

        <label htmlFor="date">Pick a date</label>
        <h6 className="text-gray-500 font-weight-light" style={{ marginBottom: 40 }}>
          You will pay <span className="text-gray-900">${itemDetails.price * data.duration} USD</span> per{" "}
          <span className="text-gray-900">
            {data.duration} {itemDetails.unit}
          </span>
        </h6>
        <Button className="btn" hasShadow isPrimary isBlock onClick={this.startBooking}>
          Continue to Book
        </Button>
      </div>
    );
  }
}

BookingForm.propTypes = {
  itemDetails: propTypes.object,
  startBooking: propTypes.func,
};

export default withRouter(BookingForm);
