import React, { Component } from "react";
import Header from "parts/Header";
import Button from "elements/Button/Button";
// import Stepper, { Numbering, Meta, MainContent, Controller } from "elements/Stepper";
import Stepper from "elements/Stepper";
import { Numbering, Meta, MainContent, Controller } from "elements/Stepper";
import BookingInformation from "parts/Checkout/BookingInformation";
import Payment from "parts/Checkout/Payment";
import Completed from "parts/Checkout/Completed";

import ItemDetails from "json/itemDetails.json";

class Checkout extends Component {
  state = {
    data: {
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      proofPayment: "",
      bankName: "",
      bankHolder: "",
    },
  };

  onChange = (e) => {
    this.setState({
      data: {
        ...this.state.data,
        [e.target.name]: e.target.value,
      },
    });
  };

  componentDidMount() {
    window.scroll(0, 0);
    document.title = "GRH | Checkout";
  }

  render() {
    const { data } = this.state;

    const checkout = {
      duration: 3,
    };

    const steps = {
      bookingInformation: {
        title: "Booking Information",
        description: "Please fill up the blank fields below",
        content: <BookingInformation data={data} checkout={checkout} ItemDetails={ItemDetails} onChange={this.onChange} />,
      },
      payment: {
        title: "Payment",
        description: "Kindly follow the instructions below",
        content: <Payment data={data} ItemDetails={ItemDetails} checkout={checkout} onChange={this.onChange} />,
      },
      completed: {
        title: "Yeay, Completed",
        description: null,
        content: <Completed />,
      },
    };

    return (
      <>
        <Header isCentered />
        <Stepper steps={steps}>
          {(prevStep, nextStep, CurrentStep, steps) => {
            <>
              <Numbering data={steps} current={CurrentStep} style={{ marginBottom: 50 }} />

              <Meta data={steps} current={CurrentStep} />

              <MainContent data={steps} current={CurrentStep} />

              {CurrentStep === "bookingInformation" && (
                <Controller>
                  {data.firstName !== "" && data.lastName !== "" && data.email !== "" && data.phone !== "" && (
                    <Button className="btn mb-3" type="button" isBlock isPrimary hasShadow onClick={nextStep}>
                      Continue to Book
                    </Button>
                  )}
                  <Button className="btn" type="link" isBlock isLight href={`/details/${ItemDetails._id}`}></Button>
                </Controller>
              )}

              {CurrentStep === "payment" && (
                <Controller>
                  {data.proofPayment !== "" && data.bankName !== "" && data.bankHolder !== "" && (
                    <Button className="btn mb-3" type="button" isBlock isPrimary hasShadow onClick={nextStep}>
                      Continue to Book
                    </Button>
                  )}
                  <Button className="btn" type="button" isBlock isLight onClick={prevStep}>
                    Cancel
                  </Button>
                </Controller>
              )}

              {CurrentStep === "completed" && (
                <Controller>
                  <Button className="btn" type="link" isBlock isPrimary hasShadow href="">
                    Back to Home
                  </Button>
                </Controller>
              )}
            </>;
          }}
        </Stepper>
      </>
    );
  }
}

// const mapStateToProps = (state) => ({
//   checkout: state.checkout,
//   page: state.page,
// });
export default Checkout;
