import React from "react";
import BrandIcon from "parts/IconText";
import Button from "elements/Button/Button";

// import IconKerangjang from "assets/images/icons/icon_keranjang.svg";

export default function Header(props) {
  const getNavLinkClass = (path) => {
    return props.location.pathname === path ? " active" : "";
  };

  if (props.isCentered) {
    return (
      <header className="spacing-sm">
        <div className="container">
          <nav className="navbar navba-expand-lg">
            <Button className="brand-text-icon mx-auto" href="/" type="link">
              GRH
            </Button>
          </nav>
        </div>
      </header>
    );
  }

  return (
    <header className="spacing-sm">
      <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light">
          <div className="container-fluid">
            <BrandIcon />
            <div className="collapse navbar-collapse">
              <ul className="navbar-nav ml-auto">
                <li className={`nav-item${getNavLinkClass("/")}`}>
                  <Button className="nav-link" type="link" href="/">
                    Home
                  </Button>
                </li>
                <li id="#product" className={`nav-item${getNavLinkClass("/product")}`}>
                  <Button className="nav-link" type="link" href="/product">
                    Product
                  </Button>
                </li>
                <li className={`nav-item${getNavLinkClass("/category")}`}>
                  <Button className="nav-link" type="link" href="/category">
                    Category
                  </Button>
                </li>
                <li className={`nav-item${getNavLinkClass("/about")}`}>
                  <Button className="nav-link" type="link" href="/about">
                    About
                  </Button>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </div>
    </header>
  );
}
