import React from "react";
// import Fade from "react-reveal/Fade";
import InputNumber from "elements/Form/InputNumber/InputNumber";

export default function FeaturedDetails({ data }) {
  state = {
    value: "",
  };

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  return (
    <section className="container">
      <div className="row featured">
        <div className="col-lg-6">
          <div className="featured-img">
            <img src={data.imageUrl} alt="" />
          </div>
        </div>
        <div className="col-lg-6">
          <div className="featured-detail">
            <h2>{data.name}</h2>
            <p className="mt-3">${data.price}</p>
            <InputNumber onChange={this.handleChange} name="value" value={this.state.value} />
            <div className="size">
              <select name="" id="">
                <option value="Pilih Ukuran" selected disabled>
                  Pilih Ukuran
                </option>
                <option value="XS">XS</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
              </select>
            </div>
            <div className="bahan mt-4">
              <select name="" id="">
                <option value="Select bahan" selected disabled>
                  Pilih Bahan
                </option>
                <option value="Catton">Catton</option>
                <option value="Sifon">Sifon</option>
                <option value="Payet">Payet</option>
              </select>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
