import React, { Component } from "react";
import { withRouter } from "react-router-dom";

import propTypes from "prop-types";

import Button from "elements/Button/Button";
import InputNumber from "elements/Form/InputNumber/InputNumber";
import InputDate from "elements/Form/InputDate/InputDate";

class BookingForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {
        duration: 1,
        date: {
          selectDate: new Date(),
          key: "selection",
        },
      },
    };
  }

  updateData = (e) => {
    this.setState({
      ...this.state,
      data: {
        ...this.state.data,
        [e.target.name]: e.target.value,
      },
    });
  };

  startBooking = () => {
    const { data } = this.state;
    this.props.startBooking({
      _id: this.props.itemDetails._id,
      duration: data.duration,
      date: {
        selectDate: data.date.selectDate,
        endDate: data.date.endDate,
      },
    });
    this.props.history.push("/checkout");
  };

  render() {
    const { data } = this.state;
    const { itemDetails, startBooking } = this.props;

    return (
      <div className="card bordered" style={{ padding: "60px 80px" }}>
        <h4 className="mb-3">Start Booking</h4>
        <h5 className="h2 mb-4">${itemDetails.price}</h5>

        <label htmlFor="duration">Berapa Baju yang Anda ingin beli?</label>
        <InputNumber className="text-center" max={100000000000000} onChange={this.updateData} name="duration" value={data.duration} />

        <label htmlFor="date">Pick a date</label>
        <InputDate onChange={this.updateData} name="date" value={data.date} />

        <h6 className="text-gray-500 font-weight-light mt-2" style={{ marginBottom: 40 }}>
          You will pay<span className="text-gray-900">${itemDetails.price * data.duration} USD</span> per{" "}
        </h6>

        <Button className="btn" hasShadow isPrimary isBlock onClick={startBooking}>
          Continue to Book
        </Button>
      </div>
    );
  }
}

BookingForm.propTypes = {
  itemDetails: propTypes.object,
  startBooking: propTypes.func,
};

export default withRouter(BookingForm);

