import React from "react";
import Button from "elements/Button";

import "assets/scss/style.scss";

export default function Categories(data) {
  return data.map((category, index1) => {
    return (
      <section className="container" key={`category-${index1}`}>
        <h4 className="mb-3 title-category">{category.name}</h4>
        <div className="category container-grid">
          {category.items.length === 0 ? (
            <div className="row">
              <div className="col-auto align-items-center">There is no design at this category</div>
            </div>
          ) : (
            category.items.map((item, index2) => {
              return (
                <div key={`category-${index1}-item-${index2}`} className="item column-3 row-2">
                  <Button type="link" href={`/details/${category._id}`}>
                    <div className="card card-featured">
                      <figure className="img-wrapper">
                        <img src={category.imageUrl} alt={category.name} className="img-contain" />
                      </figure>
                    </div>
                  </Button>
                  <div className="meta-wrapper text-center mt-3">
                    <Button type="link" className="d-block text-white" href={`/details/${category._id}`}>
                      <h5>{category.name}</h5>
                      <p>${category.price}</p>
                    </Button>
                  </div>
                </div>
              );
            })
          )}
        </div>
      </section>
    );
  });
}
